import React, { useState, useEffect } from 'react';
import { Shield, Mail, AlertTriangle, CheckCircle, History, Key, Zap, Brain, Sparkles } from 'lucide-react';
import EmailScanner from './components/EmailScanner';
import ScanHistory from './components/ScanHistory';
import ApiKeyManager from './components/ApiKeyManager';
import Stats from './components/Stats';
import ThemeToggle from './components/ThemeToggle';
import Logo from './components/Logo';
import { ThemeProvider, useTheme } from './contexts/ThemeContext';
import './index.css';

function AppContent() {
  const [activeTab, setActiveTab] = useState('scanner');
  const [apiKey, setApiKey] = useState('');
  const [scanHistory, setScanHistory] = useState([]);
  const { isDarkMode } = useTheme();

  useEffect(() => {
    // Load API key from localStorage
    const savedApiKey = localStorage.getItem('email-guardian-api-key');
    if (savedApiKey) {
      setApiKey(savedApiKey);
    }
  }, []);

  const handleApiKeySet = (key) => {
    setApiKey(key);
    localStorage.setItem('email-guardian-api-key', key);
  };

  const handleNewScan = (scanResult) => {
    setScanHistory(prev => [scanResult, ...prev.slice(0, 49)]); // Keep last 50 scans
  };

  const tabs = [
    { 
      id: 'scanner', 
      label: 'Email Scanner', 
      icon: Mail, 
      description: 'Analyze emails for threats',
      color: 'blue'
    },
    { 
      id: 'history', 
      label: 'Scan History', 
      icon: History, 
      description: 'View past scans',
      color: 'purple'
    },
    { 
      id: 'stats', 
      label: 'Analytics', 
      icon: Zap, 
      description: 'Security insights',
      color: 'emerald'
    },
    { 
      id: 'api-key', 
      label: 'API Keys', 
      icon: Key, 
      description: 'Manage access',
      color: 'amber'
    }
  ];

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0 -z-10">
        <div className={`absolute inset-0 ${
          isDarkMode 
            ? 'bg-gradient-to-br from-slate-900 via-blue-900 to-indigo-900' 
            : 'bg-gradient-to-br from-blue-600 via-purple-600 to-indigo-700'
        }`}></div>
        
        {/* Floating Elements */}
        <div className="absolute top-20 left-10 w-32 h-32 bg-blue-400/10 rounded-full blur-xl animate-pulse"></div>
        <div className="absolute top-40 right-20 w-48 h-48 bg-purple-400/10 rounded-full blur-xl animate-pulse delay-1000"></div>
        <div className="absolute bottom-20 left-1/3 w-40 h-40 bg-indigo-400/10 rounded-full blur-xl animate-pulse delay-2000"></div>
        
        {/* Grid Pattern */}
        <div className={`absolute inset-0 opacity-10 ${
          isDarkMode ? 'bg-gray-100' : 'bg-white'
        }`} style={{
          backgroundImage: `radial-gradient(circle at 1px 1px, currentColor 1px, transparent 0)`,
          backgroundSize: '40px 40px'
        }}></div>
      </div>

      {/* Header */}
      <header className={`backdrop-blur-xl border-b transition-all duration-300 ${
        isDarkMode 
          ? 'bg-slate-900/20 border-slate-700/50' 
          : 'bg-white/10 border-white/20'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            {/* Logo */}
            <Logo size="large" showText={true} />
            
            {/* Status Indicators */}
            <div className="flex items-center space-x-4">
              {/* AI Status */}
              <div className={`flex items-center space-x-2 rounded-xl px-4 py-2 backdrop-blur-sm transition-all duration-300 ${
                isDarkMode 
                  ? 'bg-slate-800/40 hover:bg-slate-800/60' 
                  : 'bg-white/10 hover:bg-white/20'
              }`}>
                <div className="relative">
                  <Brain className={`w-5 h-5 ${isDarkMode ? 'text-blue-400' : 'text-blue-100'}`} />
                  <Sparkles className="w-3 h-3 text-yellow-400 absolute -top-1 -right-1 animate-pulse" />
                </div>
                <span className={`text-sm font-medium ${isDarkMode ? 'text-blue-200' : 'text-blue-100'}`}>
                  AI Active
                </span>
              </div>
              
              {/* API Status */}
              {apiKey && (
                <div className={`flex items-center space-x-2 rounded-xl px-4 py-2 backdrop-blur-sm transition-all duration-300 ${
                  isDarkMode 
                    ? 'bg-emerald-900/40 hover:bg-emerald-900/60' 
                    : 'bg-emerald-500/20 hover:bg-emerald-500/30'
                }`}>
                  <CheckCircle className="w-5 h-5 text-emerald-300" />
                  <span className="text-sm text-emerald-200 font-medium">Connected</span>
                </div>
              )}

              {/* Theme Toggle */}
              <ThemeToggle />
            </div>
          </div>
        </div>
      </header>

      {/* Enhanced Navigation */}
      <nav className={`backdrop-blur-xl border-b transition-all duration-300 ${
        isDarkMode 
          ? 'bg-slate-800/20 border-slate-700/30' 
          : 'bg-white/5 border-white/10'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-2 py-4">
            {tabs.map((tab) => {
              const Icon = tab.icon;
              const isActive = activeTab === tab.id;
              
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`group relative flex items-center space-x-3 px-6 py-4 rounded-xl font-medium text-sm transition-all duration-300 ${
                    isActive
                      ? isDarkMode
                        ? 'bg-slate-800/60 text-white border border-slate-600/50 shadow-lg'
                        : 'bg-white/20 text-white border border-white/30 shadow-lg backdrop-blur-sm'
                      : isDarkMode
                        ? 'text-slate-300 hover:text-white hover:bg-slate-800/30'
                        : 'text-white/80 hover:text-white hover:bg-white/10'
                  }`}
                >
                  <div className={`p-2 rounded-lg transition-all duration-300 ${
                    isActive
                      ? `bg-${tab.color}-500/20`
                      : 'bg-transparent group-hover:bg-white/10'
                  }`}>
                    <Icon className={`w-5 h-5 transition-colors duration-300 ${
                      isActive 
                        ? `text-${tab.color}-400` 
                        : isDarkMode ? 'text-slate-400' : 'text-white/80'
                    }`} />
                  </div>
                  
                  <div className="flex flex-col items-start">
                    <span className="font-semibold">{tab.label}</span>
                    <span className={`text-xs transition-colors duration-300 ${
                      isActive 
                        ? isDarkMode ? 'text-slate-400' : 'text-white/80'
                        : isDarkMode ? 'text-slate-500' : 'text-white/60'
                    }`}>
                      {tab.description}
                    </span>
                  </div>

                  {/* Active indicator */}
                  {isActive && (
                    <div className={`absolute bottom-0 left-1/2 transform -translate-x-1/2 w-2 h-2 bg-${tab.color}-400 rounded-full shadow-lg`}></div>
                  )}
                </button>
              );
            })}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* API Key Alert */}
        {!apiKey && activeTab !== 'api-key' && (
          <div className={`mb-8 rounded-2xl border-l-4 border-amber-400 p-6 backdrop-blur-sm transition-all duration-300 ${
            isDarkMode 
              ? 'bg-amber-900/20 border border-amber-700/30' 
              : 'bg-amber-500/10 border border-amber-400/20'
          }`}>
            <div className="flex items-start space-x-4">
              <div className="p-2 bg-amber-400/20 rounded-lg">
                <AlertTriangle className="w-6 h-6 text-amber-400" />
              </div>
              <div>
                <h3 className={`font-bold text-lg ${isDarkMode ? 'text-amber-200' : 'text-amber-100'}`}>
                  API Key Required
                </h3>
                <p className={`mt-2 ${isDarkMode ? 'text-amber-300' : 'text-amber-200'}`}>
                  To start protecting your emails with AI-powered analysis, please configure your API key in the{' '}
                  <button
                    onClick={() => setActiveTab('api-key')}
                    className={`font-semibold underline hover:no-underline transition-all duration-200 ${
                      isDarkMode ? 'text-amber-100 hover:text-white' : 'text-white hover:text-amber-50'
                    }`}
                  >
                    API Keys section
                  </button>
                  .
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Tab Content with Animation */}
        <div className="animate-in slide-in-from-bottom duration-500">
          {activeTab === 'scanner' && (
            <EmailScanner 
              apiKey={apiKey} 
              onNewScan={handleNewScan}
            />
          )}
          
          {activeTab === 'history' && (
            <ScanHistory 
              apiKey={apiKey} 
              localHistory={scanHistory}
            />
          )}
          
          {activeTab === 'stats' && (
            <Stats 
              scanHistory={scanHistory}
            />
          )}
          
          {activeTab === 'api-key' && (
            <ApiKeyManager 
              currentApiKey={apiKey}
              onApiKeySet={handleApiKeySet}
            />
          )}
        </div>
      </main>

      {/* Enhanced Footer */}
      <footer className={`mt-20 backdrop-blur-xl border-t transition-all duration-300 ${
        isDarkMode 
          ? 'bg-slate-800/20 border-slate-700/30' 
          : 'bg-white/5 border-white/10'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col md:flex-row items-center justify-between space-y-4 md:space-y-0">
            <div className="flex items-center space-x-4">
              <Logo size="small" showText={false} />
              <div className={`text-sm ${isDarkMode ? 'text-slate-400' : 'text-white/70'}`}>
                Â© 2024 Smart Email Guardian. Built for cybersecurity education.
              </div>
            </div>
            
            <div className={`flex items-center space-x-6 text-sm ${isDarkMode ? 'text-slate-400' : 'text-white/70'}`}>
              <div className="flex items-center space-x-2">
                <Shield className="w-4 h-4" />
                <span>Enterprise Security</span>
              </div>
              <div className="flex items-center space-x-2">
                <Brain className="w-4 h-4" />
                <span>AI-Powered</span>
              </div>
              <div className="flex items-center space-x-2">
                <CheckCircle className="w-4 h-4" />
                <span>Privacy-First</span>
              </div>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
