import React, { useState, useEffect } from 'react';
import { Shield, Mail, AlertTriangle, CheckCircle, History, Key, Zap, Brain } from 'lucide-react';
import EmailScanner from './components/EmailScanner';
import ScanHistory from './components/ScanHistory';
import ApiKeyManager from './components/ApiKeyManager';
import Stats from './components/Stats';
import './index.css';

function App() {
  const [activeTab, setActiveTab] = useState('scanner');
  const [apiKey, setApiKey] = useState('');
  const [scanHistory, setScanHistory] = useState([]);

  useEffect(() => {
    // Load API key from localStorage
    const savedApiKey = localStorage.getItem('email-guardian-api-key');
    if (savedApiKey) {
      setApiKey(savedApiKey);
    }
  }, []);

  const handleApiKeySet = (key) => {
    setApiKey(key);
    localStorage.setItem('email-guardian-api-key', key);
  };

  const handleNewScan = (scanResult) => {
    setScanHistory(prev => [scanResult, ...prev.slice(0, 49)]); // Keep last 50 scans
  };

  const tabs = [
    { id: 'scanner', label: 'Email Scanner', icon: Mail },
    { id: 'history', label: 'Scan History', icon: History },
    { id: 'stats', label: 'Statistics', icon: Zap },
    { id: 'api-key', label: 'API Keys', icon: Key }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-600 to-indigo-700">
      {/* Header */}
      <header className="bg-white/10 backdrop-blur-sm border-b border-white/20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <div className="flex items-center justify-center w-10 h-10 bg-white/20 rounded-lg">
                <Shield className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-white">Smart Email Guardian</h1>
                <p className="text-sm text-white/80">AI-Powered Spam & Phishing Detection</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2 bg-white/10 rounded-lg px-3 py-2">
                <Brain className="w-4 h-4 text-white" />
                <span className="text-sm text-white font-medium">AI Protected</span>
              </div>
              
              {apiKey && (
                <div className="flex items-center space-x-2 bg-green-500/20 rounded-lg px-3 py-2">
                  <CheckCircle className="w-4 h-4 text-green-300" />
                  <span className="text-sm text-green-300 font-medium">API Connected</span>
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white/5 backdrop-blur-sm border-b border-white/10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8">
            {tabs.map((tab) => {
              const Icon = tab.icon;
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center space-x-2 px-4 py-4 border-b-2 font-medium text-sm transition-all duration-200 ${
                    activeTab === tab.id
                      ? 'border-white text-white'
                      : 'border-transparent text-white/70 hover:text-white hover:border-white/50'
                  }`}
                >
                  <Icon className="w-4 h-4" />
                  <span>{tab.label}</span>
                </button>
              );
            })}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {!apiKey && activeTab !== 'api-key' && (
          <div className="mb-8 card p-6 border-l-4 border-yellow-500">
            <div className="flex items-center space-x-3">
              <AlertTriangle className="w-6 h-6 text-yellow-600" />
              <div>
                <h3 className="font-semibold text-gray-900">API Key Required</h3>
                <p className="text-gray-600 mt-1">
                  Please set up your API key in the{' '}
                  <button
                    onClick={() => setActiveTab('api-key')}
                    className="text-primary-600 hover:text-primary-700 font-medium underline"
                  >
                    API Keys tab
                  </button>{' '}
                  to start scanning emails.
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Tab Content */}
        {activeTab === 'scanner' && (
          <EmailScanner 
            apiKey={apiKey} 
            onNewScan={handleNewScan}
          />
        )}
        
        {activeTab === 'history' && (
          <ScanHistory 
            apiKey={apiKey} 
            localHistory={scanHistory}
          />
        )}
        
        {activeTab === 'stats' && (
          <Stats 
            scanHistory={scanHistory}
          />
        )}
        
        {activeTab === 'api-key' && (
          <ApiKeyManager 
            currentApiKey={apiKey}
            onApiKeySet={handleApiKeySet}
          />
        )}
      </main>

      {/* Footer */}
      <footer className="bg-white/5 backdrop-blur-sm border-t border-white/10 mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div className="text-white/70 text-sm">
              © 2024 Smart Email Guardian. Built for cybersecurity education.
            </div>
            <div className="flex items-center space-x-4 text-white/70 text-sm">
              <span>CPU-Only AI</span>
              <span>•</span>
              <span>Privacy-First</span>
              <span>•</span>
              <span>Open Source</span>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
