import React, { useState } from 'react';
import { Scan, Shield, Mail, AlertTriangle, CheckCircle, XCircle, Clock, Brain } from 'lucide-react';
import axios from 'axios';

const EmailScanner = ({ apiKey, onNewScan }) => {
  const [emailText, setEmailText] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [scanResult, setScanResult] = useState(null);
  const [error, setError] = useState('');

  const handleScan = async () => {
    if (!emailText.trim()) {
      setError('Please enter email content to scan');
      return;
    }
    if (!apiKey) {
      setError('API key is required');
      return;
    }
    setIsScanning(true);
    setError('');
    setScanResult(null);
    try {
      const response = await axios.post('/scan', {
        email_text: emailText,
        user_id: 'web-user'
      }, {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json'
        }
      });
      const result = response.data;
      setScanResult(result);
      onNewScan(result);
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Invalid API key. Please check your API key.');
      } else if (err.response?.status === 429) {
        setError('Rate limit exceeded. Please try again later.');
      } else {
        setError(err.response?.data?.detail || 'Failed to scan email. Please try again.');
      }
    } finally {
      setIsScanning(false);
    }
  };

  const getRiskColor = (riskLevel) => {
    switch (riskLevel) {
      case 'high': return 'text-red-400 bg-red-900/20 border-red-700';
      case 'medium': return 'text-yellow-300 bg-yellow-900/20 border-yellow-700';
      case 'low': return 'text-green-300 bg-green-900/20 border-green-700';
      default: return 'text-gray-400 bg-gray-800 border-gray-700';
    }
  };

  const getClassificationIcon = (classification) => {
    switch (classification) {
      case 'phishing': return <XCircle className="w-6 h-6 text-red-400" />;
      case 'spam': return <AlertTriangle className="w-6 h-6 text-yellow-300" />;
      case 'suspicious': return <AlertTriangle className="w-6 h-6 text-orange-400" />;
      case 'legitimate': return <CheckCircle className="w-6 h-6 text-green-400" />;
      default: return <Shield className="w-6 h-6 text-gray-500" />;
    }
  };

  return (
    <div className="space-y-10">
      {/* Main Analysis Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Email Analysis Card */}
        <div className="card bg-gray-900 border border-gray-800 p-6 shadow-xl">
          <h2 className="text-lg font-semibold text-white mb-2 flex items-center gap-2">
            <Mail className="w-5 h-5 text-purple-400" /> Email Analysis
          </h2>
          <div className="mb-2">
            <label className="text-xs text-gray-400">Raw Email Content</label>
          </div>
          <textarea
            className="textarea-dark"
            rows={10}
            placeholder={`Example:\nFrom: sender@example.com\nTo: recipient@gmail.com\nSubject: Important Security Update Required\nDate: Sun Jul 24 2025\n\nDear Customer,\n\nYour account security needs immediate attention...`}
            value={emailText}
            onChange={e => setEmailText(e.target.value)}
            disabled={isScanning}
          />
          <div className="flex items-center justify-between mt-2 text-xs text-gray-500">
            <span>{emailText.length} characters</span>
            <button
              className="btn-secondary px-3 py-1"
              onClick={() => setEmailText('')}
              disabled={isScanning}
            >
              Clear
            </button>
          </div>
          {error && (
            <div className="mt-3 p-3 bg-red-900/40 border border-red-700 rounded text-red-300 text-sm">
              {error}
            </div>
          )}
          <button
            className="btn-primary w-full mt-4"
            onClick={handleScan}
            disabled={isScanning || !emailText.trim() || !apiKey}
          >
            {isScanning ? (
              <div className="flex items-center space-x-2 justify-center">
                <div className="spinner"></div>
                <span>Analyzing Email...</span>
              </div>
            ) : (
              <span>Analyze Email</span>
            )}
          </button>
        </div>
        {/* Ready/Result Card */}
        <div className="card bg-gray-900 border border-gray-800 p-6 flex flex-col items-center justify-center min-h-[340px]">
          {!scanResult ? (
            <>
              <Shield className="w-12 h-12 text-gray-700 mb-4" />
              <h3 className="text-lg font-semibold text-white mb-2">Ready for Analysis</h3>
              <p className="text-gray-400 text-center">
                Paste an email above and click <b>Analyze Email</b> to get started with AI-powered threat detection.
              </p>
            </>
          ) : (
            <div className="w-full">
              <div className="flex items-center space-x-3 mb-4">
                <div className="flex items-center justify-center w-10 h-10 bg-gray-800 rounded-lg">
                  {getClassificationIcon(scanResult.classification)}
                </div>
                <div>
                  <h3 className="text-lg font-bold text-white">Scan Results</h3>
                  <p className="text-gray-400">{scanResult.classification.toUpperCase()} • {scanResult.risk_level.toUpperCase()}</p>
                </div>
              </div>
              <div className="mb-2">
                <span className={`inline-block px-3 py-1 rounded border text-xs font-semibold ${getRiskColor(scanResult.risk_level)}`}>
                  {scanResult.risk_level.toUpperCase()}
                </span>
              </div>
              <div className="mb-2">
                <label className="block text-xs text-gray-400">Confidence</label>
                <div className="w-full bg-gray-800 rounded h-4 relative mt-1">
                  <div
                    className={`h-4 rounded transition-all duration-700 ${
                      scanResult.confidence >= 0.7 ? 'bg-red-400' :
                      scanResult.confidence >= 0.4 ? 'bg-yellow-300' : 'bg-green-400'
                    }`}
                    style={{ width: `${Math.max(scanResult.confidence * 100, 5)}%` }}
                  ></div>
                  <span className="absolute inset-0 flex items-center justify-center text-xs text-white font-semibold">
                    {(scanResult.confidence * 100).toFixed(1)}%
                  </span>
                </div>
              </div>
              <div className="mb-2">
                <label className="block text-xs text-gray-400">Explanation</label>
                <div className="bg-gray-800 rounded p-2 text-gray-200 text-xs mt-1">
                  {scanResult.explanation}
                </div>
              </div>
              {scanResult.suspicious_patterns && scanResult.suspicious_patterns.length > 0 && (
                <div className="mb-2">
                  <label className="block text-xs text-gray-400">Suspicious Patterns</label>
                  <div className="flex flex-wrap gap-1 mt-1">
                    {scanResult.suspicious_patterns.map((pattern, idx) => (
                      <span key={idx} className="px-2 py-1 bg-yellow-900/40 text-yellow-200 rounded text-xs border border-yellow-700">
                        {pattern}
                      </span>
                    ))}
                  </div>
                </div>
              )}
              <div className="mt-2 text-xs text-gray-500">
                Scan ID: {scanResult.scan_id} • {new Date(scanResult.timestamp).toLocaleString()}
              </div>
            </div>
          )}
        </div>
      </div>
      {/* Feature Highlights */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="feature-card flex flex-col items-center">
          <AlertTriangle className="w-8 h-8 text-red-400 mb-2" />
          <h4 className="text-white font-semibold mb-1">Phishing Detection</h4>
          <p className="text-gray-400 text-sm text-center">
            Advanced AI algorithms detect sophisticated phishing attempts and social engineering tactics.
          </p>
        </div>
        <div className="feature-card flex flex-col items-center">
          <CheckCircle className="w-8 h-8 text-yellow-300 mb-2" />
          <h4 className="text-white font-semibold mb-1">Spam Classification</h4>
          <p className="text-gray-400 text-sm text-center">
            Intelligent spam detection using content analysis and sender reputation scoring.
          </p>
        </div>
        <div className="feature-card flex flex-col items-center">
          <Shield className="w-8 h-8 text-green-400 mb-2" />
          <h4 className="text-white font-semibold mb-1">Safety Verification</h4>
          <p className="text-gray-400 text-sm text-center">
            Comprehensive analysis to verify legitimate emails and prevent false positives.
          </p>
        </div>
      </div>
    </div>
  );
};

export default EmailScanner;
