import React, { useState } from 'react';
import { Scan, Shield, AlertTriangle, CheckCircle, XCircle, Clock, Brain, Mail, Zap, Target } from 'lucide-react';
import { useTheme } from '../contexts/ThemeContext';
import axios from 'axios';

const EmailScanner = ({ apiKey, onNewScan }) => {
  const [emailText, setEmailText] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [scanResult, setScanResult] = useState(null);
  const [error, setError] = useState('');
  const { isDarkMode } = useTheme();

  const sampleEmails = [
    {
      name: "Phishing Example",
      text: "URGENT: Your PayPal account has been suspended! Click here immediately to verify your account and avoid permanent closure. Limited time offer - act now!",
      type: "phishing"
    },
    {
      name: "Spam Example", 
      text: "Congratulations! You've won $1,000,000! Call now to claim your prize. No obligation, free trial. Make money fast working from home!",
      type: "spam"
    },
    {
      name: "Legitimate Example",
      text: "Hi John, Thanks for your email about the project update. I've reviewed the documents and everything looks good. Let's schedule a meeting next week to discuss the next steps. Best regards, Sarah",
      type: "legitimate"
    }
  ];

  const handleScan = async () => {
    if (!emailText.trim()) {
      setError('Please enter email content to scan');
      return;
    }
    if (!apiKey) {
      setError('API key is required');
      return;
    }
    setIsScanning(true);
    setError('');
    setScanResult(null);
    try {
      const response = await axios.post('/scan', {
        email_text: emailText,
        user_id: 'web-user'
      }, {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json'
        }
      });
      const result = response.data;
      setScanResult(result);
      onNewScan(result);
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Invalid API key. Please check your API key.');
      } else if (err.response?.status === 429) {
        setError('Rate limit exceeded. Please try again later.');
      } else {
        setError(err.response?.data?.detail || 'Failed to scan email. Please try again.');
      }
    } finally {
      setIsScanning(false);
    }
  };

  const getRiskColor = (riskLevel) => {
    if (isDarkMode) {
      switch (riskLevel) {
        case 'high': return 'text-red-400 bg-red-900/20 border-red-700';
        case 'medium': return 'text-yellow-300 bg-yellow-900/20 border-yellow-700';
        case 'low': return 'text-green-400 bg-green-900/20 border-green-700';
        default: return 'text-gray-400 bg-gray-800 border-gray-700';
      }
    } else {
      switch (riskLevel) {
        case 'high': return 'text-red-700 bg-red-50 border-red-200';
        case 'medium': return 'text-yellow-700 bg-yellow-50 border-yellow-200';
        case 'low': return 'text-green-700 bg-green-50 border-green-200';
        default: return 'text-gray-700 bg-gray-50 border-gray-200';
      }
    }
  };

  const getClassificationIcon = (classification) => {
    if (isDarkMode) {
      switch (classification) {
        case 'phishing': return <XCircle className="w-6 h-6 text-red-400" />;
        case 'spam': return <AlertTriangle className="w-6 h-6 text-yellow-300" />;
        case 'suspicious': return <AlertTriangle className="w-6 h-6 text-orange-400" />;
        case 'legitimate': return <CheckCircle className="w-6 h-6 text-green-400" />;
        default: return <Shield className="w-6 h-6 text-gray-500" />;
      }
    } else {
      switch (classification) {
        case 'phishing': return <XCircle className="w-6 h-6 text-red-500" />;
        case 'spam': return <AlertTriangle className="w-6 h-6 text-yellow-500" />;
        case 'suspicious': return <AlertTriangle className="w-6 h-6 text-orange-500" />;
        case 'legitimate': return <CheckCircle className="w-6 h-6 text-green-500" />;
        default: return <Shield className="w-6 h-6 text-gray-500" />;
      }
    }
  };

  const getSampleTypeColor = (type) => {
    switch (type) {
      case 'phishing': return 'bg-red-100 text-red-700 border-red-200';
      case 'spam': return 'bg-yellow-100 text-yellow-700 border-yellow-200';
      case 'legitimate': return 'bg-green-100 text-green-700 border-green-200';
      default: return 'bg-gray-100 text-gray-700 border-gray-200';
    }
  };

  return (
    <div className="space-y-8">
      {/* Hero Section for Scanner */}
      <div className="text-center mb-12">
        <div className="flex justify-center mb-6">
          <div className="p-4 bg-gradient-to-br from-blue-500 to-purple-600 rounded-2xl shadow-lg">
            <Mail className="w-12 h-12 text-white" />
          </div>
        </div>
        <h1 className={`text-3xl md:text-4xl font-bold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
          Email Security Scanner
        </h1>
        <p className={`text-lg ${isDarkMode ? 'text-slate-400' : 'text-gray-600'} max-w-2xl mx-auto`}>
          Analyze your emails with advanced AI algorithms to detect phishing, spam, and malicious content
        </p>
      </div>

      {/* Main Scanner Interface */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Email Input Section */}
        <div className="lg:col-span-2">
          <div className={`card p-8 ${isDarkMode ? 'dark' : ''}`}>
            <div className="mb-6">
              <h3 className={`text-xl font-semibold mb-2 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                Email Analysis
              </h3>
              <p className={`text-sm ${isDarkMode ? 'text-slate-400' : 'text-gray-600'}`}>
                Paste your email content below for comprehensive security analysis
              </p>
            </div>

            {/* Sample Emails */}
            <div className="mb-6">
              <label className={`block text-sm font-medium mb-3 ${
                isDarkMode ? 'text-slate-200' : 'text-gray-700'
              }`}>
                Quick Test Samples:
              </label>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                {sampleEmails.map((sample, index) => (
                  <button
                    key={index}
                    onClick={() => setEmailText(sample.text)}
                    className={`p-3 text-left rounded-lg border transition-all duration-200 hover:shadow-md ${
                      isDarkMode 
                        ? 'bg-slate-800 border-slate-700 hover:border-slate-600 text-slate-200' 
                        : 'bg-white border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <span className={`font-medium text-sm ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                        {sample.name}
                      </span>
                      <span className={`px-2 py-1 text-xs rounded-full border ${getSampleTypeColor(sample.type)}`}>
                        {sample.type}
                      </span>
                    </div>
                    <p className={`text-xs ${isDarkMode ? 'text-slate-400' : 'text-gray-500'} line-clamp-2`}>
                      {sample.text.substring(0, 80)}...
                    </p>
                  </button>
                ))}
              </div>
            </div>

            {/* Email Content Input */}
            <div className="mb-6">
              <label htmlFor="email-text" className={`block text-sm font-medium mb-2 ${
                isDarkMode ? 'text-slate-200' : 'text-gray-700'
              }`}>
                Email Content
              </label>
              <textarea
                id="email-text"
                value={emailText}
                onChange={(e) => setEmailText(e.target.value)}
                placeholder="Paste your email content here for analysis..."
                className={`input-field h-48 ${isDarkMode ? 'dark' : ''}`}
                disabled={isScanning}
              />
              <div className={`mt-2 flex justify-between items-center text-sm ${
                isDarkMode ? 'text-slate-400' : 'text-gray-500'
              }`}>
                <span>{emailText.length}/50,000 characters</span>
                {emailText && (
                  <button
                    onClick={() => setEmailText('')}
                    className="text-blue-600 hover:text-blue-700 font-medium"
                    disabled={isScanning}
                  >
                    Clear
                  </button>
                )}
              </div>
            </div>

            {/* Error Display */}
            {error && (
              <div className={`mb-6 p-4 rounded-lg border-l-4 border-red-500 ${
                isDarkMode 
                  ? 'bg-red-900/20 border border-red-800' 
                  : 'bg-red-50 border border-red-200'
              }`}>
                <div className="flex items-center space-x-2">
                  <AlertTriangle className="w-5 h-5 text-red-500" />
                  <span className={`font-medium ${
                    isDarkMode ? 'text-red-300' : 'text-red-700'
                  }`}>{error}</span>
                </div>
              </div>
            )}

            {/* Scan Button */}
            <button
              onClick={handleScan}
              disabled={isScanning || !emailText.trim() || !apiKey}
              className={`btn-primary w-full ${isDarkMode ? 'dark' : ''} flex items-center justify-center space-x-2 h-12`}
            >
              {isScanning ? (
                <>
                  <div className="spinner"></div>
                  <span>Analyzing Email...</span>
                </>
              ) : (
                <>
                  <Scan className="w-5 h-5" />
                  <span>Analyze Email Security</span>
                </>
              )}
            </button>
          </div>
        </div>

        {/* Results & Info Section */}
        <div className="space-y-6">
          {/* Scan Results */}
          {scanResult ? (
            <div className={`card p-6 ${isDarkMode ? 'dark' : ''}`}>
              <div className="text-center mb-6">
                <div className="flex justify-center mb-4">
                  <div className={`p-3 rounded-full ${
                    scanResult.risk_level === 'high' 
                      ? 'bg-red-100 text-red-600' 
                      : scanResult.risk_level === 'medium'
                        ? 'bg-yellow-100 text-yellow-600'
                        : 'bg-green-100 text-green-600'
                  }`}>
                    {getClassificationIcon(scanResult.classification)}
                  </div>
                </div>
                <h3 className={`text-lg font-semibold mb-2 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                  Scan Complete
                </h3>
                <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium border ${getRiskColor(scanResult.risk_level)}`}>
                  {scanResult.classification.toUpperCase()} - {scanResult.risk_level.toUpperCase()}
                </div>
              </div>

              {/* Confidence Score */}
              <div className="mb-6">
                <div className={`flex justify-between text-sm mb-2 ${
                  isDarkMode ? 'text-slate-300' : 'text-gray-600'
                }`}>
                  <span>Confidence Level</span>
                  <span className="font-medium">{(scanResult.confidence * 100).toFixed(1)}%</span>
                </div>
                <div className={`w-full h-3 rounded-full ${
                  isDarkMode ? 'bg-slate-700' : 'bg-gray-200'
                }`}>
                  <div
                    className={`h-full rounded-full transition-all duration-1000 ${
                      scanResult.confidence >= 0.7 ? 'bg-red-500' : 
                      scanResult.confidence >= 0.4 ? 'bg-yellow-500' : 'bg-green-500'
                    }`}
                    style={{ width: `${Math.max(scanResult.confidence * 100, 5)}%` }}
                  ></div>
                </div>
              </div>

              {/* Analysis Details */}
              <div className="space-y-4">
                <div>
                  <label className={`text-sm font-medium ${
                    isDarkMode ? 'text-slate-300' : 'text-gray-600'
                  }`}>Analysis</label>
                  <p className={`mt-1 text-sm ${
                    isDarkMode ? 'text-slate-200' : 'text-gray-800'
                  }`}>
                    {scanResult.explanation}
                  </p>
                </div>

                {scanResult.suspicious_patterns && scanResult.suspicious_patterns.length > 0 && (
                  <div>
                    <label className={`text-sm font-medium ${
                      isDarkMode ? 'text-slate-300' : 'text-gray-600'
                    }`}>Suspicious Patterns</label>
                    <div className="mt-2 space-y-1">
                      {scanResult.suspicious_patterns.slice(0, 3).map((pattern, index) => (
                        <div key={index} className={`px-3 py-2 rounded-lg text-sm ${
                          isDarkMode 
                            ? 'bg-yellow-900/20 text-yellow-300 border border-yellow-700' 
                            : 'bg-yellow-50 text-yellow-800 border border-yellow-200'
                        }`}>
                          {pattern}
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                <div className={`pt-4 border-t text-xs ${
                  isDarkMode ? 'border-slate-700 text-slate-400' : 'border-gray-200 text-gray-500'
                }`}>
                  <div className="flex items-center justify-between">
                    <span>Scan ID: {scanResult.scan_id}</span>
                    <div className="flex items-center space-x-1">
                      <Clock className="w-3 h-3" />
                      <span>{scanResult.processing_time_ms}ms</span>
                    </div>
                  </div>
                  <div className="mt-1">
                    {new Date(scanResult.timestamp).toLocaleString()}
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div className={`card p-6 text-center ${isDarkMode ? 'dark' : ''}`}>
              <div className="mb-4">
                <Target className={`w-12 h-12 mx-auto ${isDarkMode ? 'text-slate-600' : 'text-gray-400'}`} />
              </div>
              <h3 className={`text-lg font-medium mb-2 ${isDarkMode ? 'text-slate-300' : 'text-gray-600'}`}>
                Ready to Scan
              </h3>
              <p className={`text-sm ${isDarkMode ? 'text-slate-400' : 'text-gray-500'}`}>
                Enter email content and click "Analyze Email Security" to get started
              </p>
            </div>
          )}

          {/* Security Features */}
          <div className={`card p-6 ${isDarkMode ? 'dark' : ''}`}>
            <h4 className={`font-semibold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
              Security Features
            </h4>
            <div className="space-y-3">
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center">
                  <Brain className="w-4 h-4 text-blue-600" />
                </div>
                <div>
                  <div className={`text-sm font-medium ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    AI-Powered Detection
                  </div>
                  <div className={`text-xs ${isDarkMode ? 'text-slate-400' : 'text-gray-500'}`}>
                    Advanced machine learning
                  </div>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center">
                  <Zap className="w-4 h-4 text-green-600" />
                </div>
                <div>
                  <div className={`text-sm font-medium ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    Real-time Analysis
                  </div>
                  <div className={`text-xs ${isDarkMode ? 'text-slate-400' : 'text-gray-500'}`}>
                    Instant threat detection
                  </div>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center">
                  <Shield className="w-4 h-4 text-purple-600" />
                </div>
                <div>
                  <div className={`text-sm font-medium ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    99.8% Accuracy
                  </div>
                  <div className={`text-xs ${isDarkMode ? 'text-slate-400' : 'text-gray-500'}`}>
                    Enterprise-grade security
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmailScanner;
