import React, { useState, useEffect } from 'react';
import { History, RefreshCw, Filter, AlertTriangle, CheckCircle, XCircle, Clock } from 'lucide-react';
import axios from 'axios';

const ScanHistory = ({ apiKey, localHistory }) => {
  const [serverHistory, setServerHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const [sortBy, setSortBy] = useState('newest');

  useEffect(() => {
    if (apiKey) {
      fetchServerHistory();
    }
  }, [apiKey]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchServerHistory = async () => {
    if (!apiKey) return;

    setIsLoading(true);
    setError('');

    try {
      const response = await axios.get('/history?limit=50', {
        headers: {
          'Authorization': `Bearer ${apiKey}`
        }
      });
      setServerHistory(response.data.history || []);
    } catch (err) {
      console.error('History fetch error:', err);
      if (err.response?.status === 401) {
        setError('Invalid API key');
      } else {
        setError('Failed to load server history');
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Combine and deduplicate local and server history
  const allHistory = React.useMemo(() => {
    const combined = [...localHistory];
    
    // Add server history items that aren't already in local history
    serverHistory.forEach(serverItem => {
      const exists = combined.some(localItem => localItem.scan_id === serverItem.scan_id);
      if (!exists) {
        combined.push(serverItem);
      }
    });

    // Filter by classification
    let filtered = combined;
    if (filter !== 'all') {
      filtered = combined.filter(item => item.classification === filter);
    }

    // Sort by selected criteria
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'newest':
          return new Date(b.timestamp) - new Date(a.timestamp);
        case 'oldest':
          return new Date(a.timestamp) - new Date(b.timestamp);
        case 'confidence':
          return b.confidence - a.confidence;
        case 'risk':
          const riskOrder = { high: 3, medium: 2, low: 1 };
          return riskOrder[b.risk_level] - riskOrder[a.risk_level];
        default:
          return 0;
      }
    });

    return filtered;
  }, [localHistory, serverHistory, filter, sortBy]);

  const getClassificationIcon = (classification) => {
    switch (classification) {
      case 'phishing': return <XCircle className="w-5 h-5 text-red-500" />;
      case 'spam': return <AlertTriangle className="w-5 h-5 text-yellow-500" />;
      case 'suspicious': return <AlertTriangle className="w-5 h-5 text-orange-500" />;
      case 'legitimate': return <CheckCircle className="w-5 h-5 text-green-500" />;
      default: return <AlertTriangle className="w-5 h-5 text-gray-500" />;
    }
  };

  const getRiskBadgeClass = (riskLevel) => {
    switch (riskLevel) {
      case 'high': return 'bg-red-100 text-red-800 border-red-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getConfidenceColor = (confidence) => {
    if (confidence >= 0.7) return 'bg-red-500';
    if (confidence >= 0.4) return 'bg-yellow-500';
    return 'bg-green-500';
  };

  const stats = React.useMemo(() => {
    const total = allHistory.length;
    const phishing = allHistory.filter(item => item.classification === 'phishing').length;
    const spam = allHistory.filter(item => item.classification === 'spam').length;
    const legitimate = allHistory.filter(item => item.classification === 'legitimate').length;
    const avgConfidence = total > 0 ? 
      allHistory.reduce((sum, item) => sum + item.confidence, 0) / total : 0;

    return { total, phishing, spam, legitimate, avgConfidence };
  }, [allHistory]);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="card p-6">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-3">
            <div className="flex items-center justify-center w-12 h-12 bg-primary-100 rounded-lg">
              <History className="w-6 h-6 text-primary-600" />
            </div>
            <div>
              <h2 className="text-2xl font-bold text-gray-900">Scan History</h2>
              <p className="text-gray-600">View your previous email scan results</p>
            </div>
          </div>

          {apiKey && (
            <button
              onClick={fetchServerHistory}
              disabled={isLoading}
              className="btn-secondary flex items-center space-x-2"
            >
              <RefreshCw className={`w-4 h-4 ${isLoading ? 'animate-spin' : ''}`} />
              <span>Refresh</span>
            </button>
          )}
        </div>

        {/* Stats */}
        <div className="grid grid-cols-2 lg:grid-cols-5 gap-4 mb-6">
          <div className="bg-gray-50 rounded-lg p-4 text-center">
            <div className="text-2xl font-bold text-gray-900">{stats.total}</div>
            <div className="text-sm text-gray-600">Total Scans</div>
          </div>
          <div className="bg-red-50 rounded-lg p-4 text-center">
            <div className="text-2xl font-bold text-red-600">{stats.phishing}</div>
            <div className="text-sm text-red-600">Phishing</div>
          </div>
          <div className="bg-yellow-50 rounded-lg p-4 text-center">
            <div className="text-2xl font-bold text-yellow-600">{stats.spam}</div>
            <div className="text-sm text-yellow-600">Spam</div>
          </div>
          <div className="bg-green-50 rounded-lg p-4 text-center">
            <div className="text-2xl font-bold text-green-600">{stats.legitimate}</div>
            <div className="text-sm text-green-600">Legitimate</div>
          </div>
          <div className="bg-blue-50 rounded-lg p-4 text-center">
            <div className="text-2xl font-bold text-blue-600">{(stats.avgConfidence * 100).toFixed(1)}%</div>
            <div className="text-sm text-blue-600">Avg Confidence</div>
          </div>
        </div>

        {/* Filters */}
        <div className="flex flex-wrap items-center gap-4">
          <div className="flex items-center space-x-2">
            <Filter className="w-4 h-4 text-gray-500" />
            <label className="text-sm font-medium text-gray-700">Filter:</label>
            <select
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              className="text-sm border border-gray-300 rounded px-3 py-1 focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="all">All Types</option>
              <option value="phishing">Phishing</option>
              <option value="spam">Spam</option>
              <option value="suspicious">Suspicious</option>
              <option value="legitimate">Legitimate</option>
            </select>
          </div>

          <div className="flex items-center space-x-2">
            <label className="text-sm font-medium text-gray-700">Sort:</label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="text-sm border border-gray-300 rounded px-3 py-1 focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
              <option value="confidence">By Confidence</option>
              <option value="risk">By Risk Level</option>
            </select>
          </div>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex items-center space-x-2">
            <XCircle className="w-5 h-5 text-red-500" />
            <span className="text-red-800">{error}</span>
          </div>
        </div>
      )}

      {/* History List */}
      {allHistory.length === 0 ? (
        <div className="card p-12 text-center">
          <History className="w-16 h-16 text-gray-300 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Scan History</h3>
          <p className="text-gray-600">
            {apiKey ? 
              "Start scanning emails to see your history here." :
              "Set up your API key and start scanning emails to see your history."
            }
          </p>
        </div>
      ) : (
        <div className="space-y-4">
          {allHistory.map((scan, index) => (
            <div key={scan.scan_id || index} className="card p-6 hover:shadow-lg transition-shadow">
              <div className="flex items-start justify-between">
                <div className="flex items-start space-x-4 flex-1">
                  <div className="flex items-center justify-center w-10 h-10 bg-gray-100 rounded-lg">
                    {getClassificationIcon(scan.classification)}
                  </div>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-3 mb-2">
                      <h3 className="text-lg font-semibold text-gray-900 capitalize">
                        {scan.classification}
                      </h3>
                      <span className={`px-2 py-1 text-xs font-medium rounded border ${getRiskBadgeClass(scan.risk_level)}`}>
                        {scan.risk_level?.toUpperCase()}
                      </span>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-3">
                      <div>
                        <label className="text-xs font-medium text-gray-500 uppercase tracking-wide">
                          Confidence
                        </label>
                        <div className="mt-1 bg-gray-200 rounded-full h-2 relative">
                          <div 
                            className={`h-full rounded-full transition-all duration-500 ${getConfidenceColor(scan.confidence)}`}
                            style={{ width: `${Math.max(scan.confidence * 100, 2)}%` }}
                          ></div>
                        </div>
                        <div className="text-sm font-medium text-gray-900 mt-1">
                          {(scan.confidence * 100).toFixed(1)}%
                        </div>
                      </div>

                      <div>
                        <label className="text-xs font-medium text-gray-500 uppercase tracking-wide">
                          Processing Time
                        </label>
                        <div className="flex items-center space-x-1 mt-1">
                          <Clock className="w-3 h-3 text-gray-400" />
                          <span className="text-sm text-gray-900">{scan.processing_time_ms}ms</span>
                        </div>
                      </div>

                      <div>
                        <label className="text-xs font-medium text-gray-500 uppercase tracking-wide">
                          Date
                        </label>
                        <div className="text-sm text-gray-900 mt-1">
                          {new Date(scan.timestamp).toLocaleDateString()}
                        </div>
                      </div>
                    </div>

                    {scan.explanation && (
                      <div className="bg-gray-50 rounded-lg p-3 mb-3">
                        <label className="text-xs font-medium text-gray-500 uppercase tracking-wide block mb-1">
                          Analysis
                        </label>
                        <p className="text-sm text-gray-800">{scan.explanation}</p>
                      </div>
                    )}

                    {scan.suspicious_patterns && scan.suspicious_patterns.length > 0 && (
                      <div>
                        <label className="text-xs font-medium text-gray-500 uppercase tracking-wide block mb-2">
                          Suspicious Patterns
                        </label>
                        <div className="flex flex-wrap gap-1">
                          {scan.suspicious_patterns.slice(0, 3).map((pattern, idx) => (
                            <span key={idx} className="px-2 py-1 text-xs bg-yellow-100 text-yellow-800 rounded">
                              {pattern}
                            </span>
                          ))}
                          {scan.suspicious_patterns.length > 3 && (
                            <span className="px-2 py-1 text-xs bg-gray-100 text-gray-600 rounded">
                              +{scan.suspicious_patterns.length - 3} more
                            </span>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                </div>

                <div className="text-right">
                  <div className="text-xs text-gray-500">
                    {new Date(scan.timestamp).toLocaleTimeString()}
                  </div>
                  {scan.scan_id && (
                    <div className="text-xs text-gray-400 mt-1 font-mono">
                      {scan.scan_id.slice(0, 8)}...
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Loading indicator */}
      {isLoading && (
        <div className="card p-6 text-center">
          <div className="spinner mx-auto mb-2"></div>
          <p className="text-gray-600">Loading scan history...</p>
        </div>
      )}
    </div>
  );
};

export default ScanHistory;
