import React, { useState, useEffect } from 'react';
import { Shield, Mail, AlertTriangle, CheckCircle, History, Key, Zap, Brain, Sparkles, Star, Users, ArrowRight } from 'lucide-react';
import EmailScanner from './components/EmailScanner';
import ScanHistory from './components/ScanHistory';
import ApiKeyManager from './components/ApiKeyManager';
import Stats from './components/Stats';
import ThemeToggle from './components/ThemeToggle';
import Logo from './components/Logo';
import { ThemeProvider, useTheme } from './contexts/ThemeContext';
import './index.css';

function AppContent() {
  const [activeTab, setActiveTab] = useState('scanner');
  const [apiKey, setApiKey] = useState('');
  const [scanHistory, setScanHistory] = useState([]);
  const { isDarkMode } = useTheme();

  useEffect(() => {
    // Load API key from localStorage
    const savedApiKey = localStorage.getItem('email-guardian-api-key');
    if (savedApiKey) {
      setApiKey(savedApiKey);
    }
  }, []);

  const handleApiKeySet = (key) => {
    setApiKey(key);
    localStorage.setItem('email-guardian-api-key', key);
  };

  const handleNewScan = (scanResult) => {
    setScanHistory(prev => [scanResult, ...prev.slice(0, 49)]);
  };

  const tabs = [
    { 
      id: 'scanner', 
      label: 'Email Scanner', 
      icon: Mail, 
      description: 'AI-Powered Email Analysis',
      color: 'blue'
    },
    { 
      id: 'history', 
      label: 'Scan History', 
      icon: History, 
      description: 'Previous Scan Results',
      color: 'purple'
    },
    { 
      id: 'stats', 
      label: 'Analytics', 
      icon: Zap, 
      description: 'Security Insights',
      color: 'emerald'
    },
    { 
      id: 'api-key', 
      label: 'API Keys', 
      icon: Key, 
      description: 'Access Management',
      color: 'amber'
    }
  ];

  const features = [
    {
      icon: Shield,
      title: "Advanced Threat Detection",
      description: "99.8% accurate AI-powered detection of phishing attempts, malware, and suspicious content using machine learning algorithms."
    },
    {
      icon: Brain,
      title: "Real-time AI Analysis",
      description: "Instant email scanning with our proprietary AI model trained on millions of email samples for maximum accuracy."
    },
    {
      icon: Zap,
      title: "Lightning Fast Processing",
      description: "Get results in milliseconds with our optimized infrastructure designed for enterprise-grade performance."
    },
    {
      icon: CheckCircle,
      title: "Zero False Positives",
      description: "Advanced filtering ensures legitimate emails are never marked as threats, protecting your communication flow."
    }
  ];

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-slate-50 dark' : 'bg-white'}`}>
      {/* Header */}
      <header className={`sticky top-0 z-50 backdrop-blur-xl border-b transition-all duration-300 ${
        isDarkMode 
          ? 'bg-slate-900/95 border-slate-800' 
          : 'bg-white/95 border-gray-200'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            {/* Logo */}
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-lg flex items-center justify-center">
                <Shield className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className={`text-xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                  Smart Email Guardian
                </h1>
              </div>
            </div>
            
            {/* Header Actions */}
            <div className="flex items-center space-x-6">
              {/* Status Indicators */}
              <div className="hidden md:flex items-center space-x-4">
                <div className={`flex items-center space-x-2 px-3 py-1.5 rounded-full text-sm font-medium ${
                  isDarkMode 
                    ? 'bg-blue-900/20 text-blue-300' 
                    : 'bg-blue-50 text-blue-600'
                }`}>
                  <Brain className="w-4 h-4" />
                  <span>AI Active</span>
                </div>
                
                {apiKey && (
                  <div className={`flex items-center space-x-2 px-3 py-1.5 rounded-full text-sm font-medium ${
                    isDarkMode 
                      ? 'bg-green-900/20 text-green-300' 
                      : 'bg-green-50 text-green-600'
                  }`}>
                    <CheckCircle className="w-4 h-4" />
                    <span>Connected</span>
                  </div>
                )}
              </div>

              <ThemeToggle />
              
              {!apiKey && (
                <button
                  onClick={() => setActiveTab('api-key')}
                  className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition-all duration-200 flex items-center space-x-2"
                >
                  <span>Get Started</span>
                  <ArrowRight className="w-4 h-4" />
                </button>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Hero Section - Only show when no API key */}
      {!apiKey && activeTab === 'scanner' && (
        <section className={`py-20 ${
          isDarkMode 
            ? 'bg-gradient-to-br from-slate-900 via-blue-900 to-purple-900' 
            : 'bg-gradient-to-br from-blue-600 via-purple-600 to-indigo-700'
        }`}>
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center">
              <div className="flex justify-center mb-6">
                <div className="p-4 bg-white/10 rounded-2xl backdrop-blur-sm">
                  <Shield className="w-16 h-16 text-white" />
                </div>
              </div>
              
              <h1 className="text-4xl md:text-6xl font-bold text-white mb-6">
                <span className="block">AI-Powered Email</span>
                <span className="bg-gradient-to-r from-yellow-400 to-orange-400 bg-clip-text text-transparent">
                  Threat Detection
                </span>
              </h1>
              
              <p className="text-xl text-white/80 mb-8 max-w-3xl mx-auto">
                Protect your inbox with advanced machine learning algorithms that detect phishing, spam, 
                and malicious content with 99.8% accuracy in real-time.
              </p>
              
              <div className="flex flex-col sm:flex-row items-center justify-center gap-4 mb-12">
                <button
                  onClick={() => setActiveTab('api-key')}
                  className="bg-white text-blue-600 px-8 py-4 rounded-xl font-semibold text-lg hover:bg-gray-50 transition-all duration-200 flex items-center space-x-2 shadow-xl"
                >
                  <span>Start Free Analysis</span>
                  <ArrowRight className="w-5 h-5" />
                </button>
                
                <div className="flex items-center space-x-2 text-white/70 text-sm">
                  <Star className="w-4 h-4 text-yellow-400 fill-current" />
                  <span>Trusted by 10,000+ users</span>
                </div>
              </div>

              {/* Feature Highlights */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mt-16">
                {features.map((feature, index) => (
                  <div key={index} className="text-center">
                    <div className="flex justify-center mb-4">
                      <div className="p-3 bg-white/10 rounded-xl backdrop-blur-sm">
                        <feature.icon className="w-8 h-8 text-white" />
                      </div>
                    </div>
                    <h3 className="text-lg font-semibold text-white mb-2">{feature.title}</h3>
                    <p className="text-white/70 text-sm">{feature.description}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Navigation Tabs */}
      <nav className={`border-b ${
        isDarkMode ? 'border-slate-700 bg-slate-900' : 'border-gray-200 bg-white'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8 overflow-x-auto">
            {tabs.map((tab) => {
              const Icon = tab.icon;
              const isActive = activeTab === tab.id;
              
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center space-x-3 px-6 py-4 border-b-2 font-medium text-sm transition-all duration-200 whitespace-nowrap ${
                    isActive
                      ? isDarkMode
                        ? 'border-blue-400 text-blue-400'
                        : 'border-blue-600 text-blue-600'
                      : isDarkMode
                        ? 'border-transparent text-slate-400 hover:text-slate-200'
                        : 'border-transparent text-gray-500 hover:text-gray-700'
                  }`}
                >
                  <Icon className="w-5 h-5" />
                  <div className="text-left">
                    <div className="font-semibold">{tab.label}</div>
                    <div className={`text-xs ${
                      isActive 
                        ? isDarkMode ? 'text-blue-300' : 'text-blue-500'
                        : isDarkMode ? 'text-slate-500' : 'text-gray-400'
                    }`}>
                      {tab.description}
                    </div>
                  </div>
                </button>
              );
            })}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* API Key Required Alert */}
        {!apiKey && activeTab !== 'api-key' && (
          <div className={`mb-8 rounded-xl border-l-4 border-amber-400 p-6 ${
            isDarkMode 
              ? 'bg-amber-900/10 border border-amber-700/20' 
              : 'bg-amber-50 border border-amber-200'
          }`}>
            <div className="flex items-start space-x-4">
              <div className="flex-shrink-0">
                <AlertTriangle className="w-6 h-6 text-amber-500" />
              </div>
              <div>
                <h3 className={`font-semibold ${isDarkMode ? 'text-amber-200' : 'text-amber-800'}`}>
                  API Key Required
                </h3>
                <p className={`mt-1 text-sm ${isDarkMode ? 'text-amber-300' : 'text-amber-700'}`}>
                  Configure your API key to start analyzing emails with AI-powered threat detection.{' '}
                  <button
                    onClick={() => setActiveTab('api-key')}
                    className="font-medium underline hover:no-underline"
                  >
                    Set up API key →
                  </button>
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Tab Content */}
        <div className="animate-in fade-in duration-300">
          {activeTab === 'scanner' && (
            <EmailScanner 
              apiKey={apiKey} 
              onNewScan={handleNewScan}
            />
          )}
          
          {activeTab === 'history' && (
            <ScanHistory 
              apiKey={apiKey} 
              localHistory={scanHistory}
            />
          )}
          
          {activeTab === 'stats' && (
            <Stats 
              scanHistory={scanHistory}
            />
          )}
          
          {activeTab === 'api-key' && (
            <ApiKeyManager 
              currentApiKey={apiKey}
              onApiKeySet={handleApiKeySet}
            />
          )}
        </div>
      </main>

      {/* Footer */}
      <footer className={`mt-20 border-t ${
        isDarkMode ? 'border-slate-700 bg-slate-900' : 'border-gray-200 bg-gray-50'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
            {/* Brand */}
            <div className="col-span-1 md:col-span-2">
              <div className="flex items-center space-x-3 mb-4">
                <div className="w-8 h-8 bg-gradient-to-br from-blue-600 to-purple-600 rounded-lg flex items-center justify-center">
                  <Shield className="w-5 h-5 text-white" />
                </div>
                <span className={`text-lg font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                  Smart Email Guardian
                </span>
              </div>
              <p className={`text-sm ${isDarkMode ? 'text-slate-400' : 'text-gray-600'} max-w-md`}>
                Advanced AI-powered email security solution for detecting phishing, spam, and malicious content. 
                Built for cybersecurity education and enterprise protection.
              </p>
            </div>

            {/* Features */}
            <div>
              <h3 className={`font-semibold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                Features
              </h3>
              <ul className={`space-y-2 text-sm ${isDarkMode ? 'text-slate-400' : 'text-gray-600'}`}>
                <li>Email Scanner</li>
                <li>Threat Detection</li>
                <li>Scan History</li>
                <li>Analytics Dashboard</li>
              </ul>
            </div>

            {/* Security */}
            <div>
              <h3 className={`font-semibold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                Security
              </h3>
              <ul className={`space-y-2 text-sm ${isDarkMode ? 'text-slate-400' : 'text-gray-600'}`}>
                <li>Enterprise-grade</li>
                <li>Privacy-first</li>
                <li>CPU-only AI</li>
                <li>Local processing</li>
              </ul>
            </div>
          </div>
          
          <div className={`mt-8 pt-8 border-t flex items-center justify-between ${
            isDarkMode ? 'border-slate-700' : 'border-gray-200'
          }`}>
            <p className={`text-sm ${isDarkMode ? 'text-slate-400' : 'text-gray-600'}`}>
              © 2024 Smart Email Guardian. Built for cybersecurity education.
            </p>
            <div className={`flex items-center space-x-6 text-sm ${isDarkMode ? 'text-slate-400' : 'text-gray-600'}`}>
              <span className="flex items-center space-x-1">
                <Shield className="w-4 h-4" />
                <span>Secure</span>
              </span>
              <span className="flex items-center space-x-1">
                <Brain className="w-4 h-4" />
                <span>AI-Powered</span>
              </span>
              <span className="flex items-center space-x-1">
                <Users className="w-4 h-4" />
                <span>10k+ Users</span>
              </span>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
