import React, { useState } from 'react';
import { Key, Plus, Eye, EyeOff, Copy, CheckCircle, XCircle } from 'lucide-react';
import axios from 'axios';

const ApiKeyManager = ({ currentApiKey, onApiKeySet }) => {
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [keyName, setKeyName] = useState('');
  const [keyDescription, setKeyDescription] = useState('');
  const [isCreating, setIsCreating] = useState(false);
  const [newApiKey, setNewApiKey] = useState('');
  const [showCurrentKey, setShowCurrentKey] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleCreateKey = async () => {
    if (!keyName.trim()) {
      setError('Key name is required');
      return;
    }

    setIsCreating(true);
    setError('');
    setSuccess('');

    try {
      const response = await axios.post('/create-key', {
        name: keyName.trim(),
        description: keyDescription.trim() || undefined
      });

      setNewApiKey(response.data.api_key);
      setSuccess('API key created successfully!');
      setKeyName('');
      setKeyDescription('');
      setShowCreateForm(false);
    } catch (err) {
      console.error('Create key error:', err);
      setError(err.response?.data?.detail || 'Failed to create API key');
    } finally {
      setIsCreating(false);
    }
  };

  const handleUseKey = (key) => {
    onApiKeySet(key);
    setNewApiKey('');
    setSuccess('API key set successfully!');
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text).then(() => {
      setSuccess('Copied to clipboard!');
      setTimeout(() => setSuccess(''), 2000);
    });
  };

  const maskKey = (key) => {
    if (!key) return '';
    return key.substring(0, 8) + '...' + key.substring(key.length - 8);
  };

  return (
    <div className="space-y-6">
      {/* Current API Key Status */}
      <div className="card p-6">
        <div className="flex items-center space-x-3 mb-6">
          <div className="flex items-center justify-center w-12 h-12 bg-primary-100 rounded-lg">
            <Key className="w-6 h-6 text-primary-600" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-gray-900">API Key Management</h2>
            <p className="text-gray-600">Manage your Email Guardian API access keys</p>
          </div>
        </div>

        {currentApiKey ? (
          <div className="bg-green-50 border border-green-200 rounded-lg p-4">
            <div className="flex items-center space-x-2 mb-3">
              <CheckCircle className="w-5 h-5 text-green-500" />
              <span className="text-green-800 font-medium">API Key Active</span>
            </div>
            
            <div className="space-y-3">
              <div className="flex items-center space-between">
                <label className="text-sm font-medium text-green-700">Current API Key:</label>
              </div>
              
              <div className="flex items-center space-x-2">
                <div className="flex-1 px-3 py-2 bg-white border border-green-300 rounded text-sm font-mono">
                  {showCurrentKey ? currentApiKey : maskKey(currentApiKey)}
                </div>
                <button
                  onClick={() => setShowCurrentKey(!showCurrentKey)}
                  className="p-2 text-green-600 hover:text-green-700 hover:bg-green-100 rounded"
                >
                  {showCurrentKey ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                </button>
                <button
                  onClick={() => copyToClipboard(currentApiKey)}
                  className="p-2 text-green-600 hover:text-green-700 hover:bg-green-100 rounded"
                >
                  <Copy className="w-4 h-4" />
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-center space-x-2 mb-2">
              <XCircle className="w-5 h-5 text-yellow-500" />
              <span className="text-yellow-800 font-medium">No API Key Set</span>
            </div>
            <p className="text-yellow-700 text-sm">
              You need an API key to use the email scanning service. Create one below or enter an existing key.
            </p>
          </div>
        )}
      </div>

      {/* Success/Error Messages */}
      {success && (
        <div className="bg-green-50 border border-green-200 rounded-lg p-4">
          <div className="flex items-center space-x-2">
            <CheckCircle className="w-5 h-5 text-green-500" />
            <span className="text-green-800">{success}</span>
          </div>
        </div>
      )}

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex items-center space-x-2">
            <XCircle className="w-5 h-5 text-red-500" />
            <span className="text-red-800">{error}</span>
          </div>
        </div>
      )}

      {/* New API Key Display */}
      {newApiKey && (
        <div className="card p-6 border-2 border-green-200 bg-green-50">
          <h3 className="text-lg font-bold text-green-800 mb-4">🎉 New API Key Created!</h3>
          
          <div className="bg-white border border-green-300 rounded-lg p-4 mb-4">
            <label className="block text-sm font-medium text-green-700 mb-2">
              Your New API Key:
            </label>
            <div className="flex items-center space-x-2">
              <div className="flex-1 px-3 py-2 bg-gray-50 border border-gray-300 rounded text-sm font-mono break-all">
                {newApiKey}
              </div>
              <button
                onClick={() => copyToClipboard(newApiKey)}
                className="p-2 text-green-600 hover:text-green-700 hover:bg-green-100 rounded"
                title="Copy to clipboard"
              >
                <Copy className="w-4 h-4" />
              </button>
            </div>
          </div>

          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 mb-4">
            <p className="text-yellow-800 text-sm font-medium">
              ⚠️ Important: Store this key securely! It won't be shown again.
            </p>
          </div>

          <div className="flex space-x-3">
            <button
              onClick={() => handleUseKey(newApiKey)}
              className="btn-primary"
            >
              Use This Key
            </button>
            <button
              onClick={() => setNewApiKey('')}
              className="btn-secondary"
            >
              I'll Save It Later
            </button>
          </div>
        </div>
      )}

      {/* Create New API Key */}
      <div className="card p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">Create New API Key</h3>
          <button
            onClick={() => setShowCreateForm(!showCreateForm)}
            className="btn-secondary flex items-center space-x-2"
          >
            <Plus className="w-4 h-4" />
            <span>{showCreateForm ? 'Cancel' : 'Create Key'}</span>
          </button>
        </div>

        {showCreateForm && (
          <div className="space-y-4">
            <div>
              <label htmlFor="key-name" className="block text-sm font-medium text-gray-700 mb-2">
                Key Name *
              </label>
              <input
                id="key-name"
                type="text"
                value={keyName}
                onChange={(e) => setKeyName(e.target.value)}
                placeholder="e.g., My Web App Key"
                className="input-field"
                disabled={isCreating}
              />
            </div>

            <div>
              <label htmlFor="key-description" className="block text-sm font-medium text-gray-700 mb-2">
                Description (Optional)
              </label>
              <textarea
                id="key-description"
                value={keyDescription}
                onChange={(e) => setKeyDescription(e.target.value)}
                placeholder="Optional description for this API key"
                className="input-field h-20"
                disabled={isCreating}
              />
            </div>

            <button
              onClick={handleCreateKey}
              disabled={isCreating || !keyName.trim()}
              className="btn-primary"
            >
              {isCreating ? (
                <div className="flex items-center space-x-2">
                  <div className="spinner"></div>
                  <span>Creating Key...</span>
                </div>
              ) : (
                'Create API Key'
              )}
            </button>
          </div>
        )}
      </div>

      {/* Manual API Key Entry */}
      <div className="card p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Use Existing API Key</h3>
        <p className="text-gray-600 mb-4">
          If you already have an API key, you can enter it here to start using the service.
        </p>

        <div className="space-y-4">
          <div>
            <label htmlFor="existing-key" className="block text-sm font-medium text-gray-700 mb-2">
              API Key
            </label>
            <input
              id="existing-key"
              type="password"
              placeholder="Paste your existing API key here"
              className="input-field"
              onPaste={(e) => {
                setTimeout(() => {
                  const key = e.target.value;
                  if (key.trim()) {
                    handleUseKey(key.trim());
                    e.target.value = '';
                  }
                }, 100);
              }}
            />
          </div>
        </div>
      </div>

      {/* API Documentation */}
      <div className="card p-6 bg-gray-50">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">API Usage Information</h3>
        
        <div className="space-y-3 text-sm text-gray-600">
          <div className="flex items-start space-x-2">
            <span className="font-medium text-gray-700">Endpoint:</span>
            <code className="bg-gray-200 px-2 py-1 rounded text-xs">POST /scan</code>
          </div>
          
          <div className="flex items-start space-x-2">
            <span className="font-medium text-gray-700">Header:</span>
            <code className="bg-gray-200 px-2 py-1 rounded text-xs">Authorization: Bearer YOUR_API_KEY</code>
          </div>
          
          <div className="flex items-start space-x-2">
            <span className="font-medium text-gray-700">Rate Limit:</span>
            <span>100 requests per hour</span>
          </div>
          
          <div className="flex items-start space-x-2">
            <span className="font-medium text-gray-700">Max Email Size:</span>
            <span>50KB per request</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ApiKeyManager;
